INSERT INTO tb_game_list (name) VALUES ('Aventura e RPG');
INSERT INTO tb_game_list (name) VALUES ('Jogos de plataforma');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Mass Effect Trilogy', 4.8, 2012, 'Role-playing (RPG), Shooter', 'XBox, Playstation, PC','https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/1.png', 'Uma épica jornada intergaláctica, onde suas escolhas moldam o destino da galáxia.', 'Mass Effect Trilogy é uma saga de RPG e tiro em terceira pessoa, onde os jogadores assumem o papel do Comandante Shepard. Suas escolhas determinam o destino de civilizações inteiras em uma narrativa envolvente e emocionante.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Red Dead Redemption 2', 4.7, 2018, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/2.png', 'Um mundo aberto cheio de aventura e perigo no Velho Oeste.', 'Red Dead Redemption 2 é um jogo de ação e aventura ambientado no fim do século XIX. Os jogadores exploram um vasto e detalhado mundo aberto, enfrentando desafios e tomando decisões morais enquanto seguem a história de Arthur Morgan e a gangue Van der Linde.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('The Witcher 3: Wild Hunt', 4.7, 2014, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/3.png', 'Uma jornada épica em um mundo aberto cheio de criaturas lendárias e aventuras.', 'The Witcher 3: Wild Hunt segue a história de Geralt de Rívia, um caçador de monstros, enquanto ele busca encontrar sua filha adotiva em um vasto e detalhado mundo aberto. O jogo combina combate intenso, narrativa profunda e exploração de tirar o fôlego.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sekiro: Shadows Die Twice', 3.8, 2019, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/4.png', 'Um jogo de ação e aventura com combate desafiador ambientado no Japão feudal.', 'Sekiro: Shadows Die Twice coloca você no papel de um guerreiro shinobi que busca vingança contra seus inimigos. Com mecânicas de combate únicas e uma narrativa rica, o jogo oferece uma experiência intensa e recompensadora.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ghost of Tsushima', 4.6, 2020, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/5.png' , 'Uma jornada épica de vingança e honra no Japão feudal.', 'Ghost of Tsushima segue a história de Jin Sakai, um samurai que deve abandonar suas tradições para proteger sua terra natal da invasão mongol. Com paisagens deslumbrantes e combate visceral, o jogo oferece uma imersão profunda na cultura e na história do Japão.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Super Mario World', 4.7, 1990, 'Platform', 'Super Ness, PC','https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/6.png', 'Uma aventura clássica de plataforma com Mario e seus amigos.', 'Super Mario World é um jogo de plataforma onde os jogadores ajudam Mario a salvar a Princesa Peach das garras de Bowser. Com gráficos coloridos e jogabilidade envolvente, o jogo é um clássico amado por gerações de jogadores.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Hollow Knight', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/7.png', 'Uma aventura de plataforma e exploração em um mundo subterrâneo misterioso.', 'Hollow Knight é um jogo de ação e aventura onde os jogadores exploram um vasto e enigmático reino subterrâneo. Com combate preciso, belos gráficos em 2D e uma história intrigante, o jogo é uma verdadeira obra-prima indie.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ori and the Blind Forest', 4, 2015, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/8.png' , 'Uma emocionante jornada de plataforma em um mundo mágico e visualmente deslumbrante.', 'Ori and the Blind Forest é um jogo de plataforma que combina gráficos impressionantes, uma trilha sonora emocionante e uma narrativa tocante. Os jogadores guiam Ori através de desafios e puzzles em um mundo encantador e cheio de perigos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Cuphead', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/9.png' , 'Um jogo de ação e plataforma com um estilo de animação dos anos 1930.', 'Cuphead é um jogo de ação e plataforma conhecido por sua estética única de desenhos animados dos anos 1930 e sua dificuldade elevada. Os jogadores enfrentam chefes desafiadores em uma série de lutas intensas e visualmente impressionantes.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sonic CD', 4, 1993, 'Platform', 'Sega CD, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/10.png','Uma clássica aventura de plataforma com Sonic em sua luta contra Dr. Robotnik.', 'Sonic CD é um jogo de plataforma onde os jogadores correm através de níveis cheios de loopings e obstáculos para impedir os planos malignos do Dr. Robotnik. Com mecânicas de viagem no tempo e trilha sonora memorável, o jogo é um destaque na série Sonic.');
